        let cell = tableView.dequeueReusableCellWithIdentifier(cellIdentifier, forIndexPath: indexPath) as! ChemicalsTableViewCell
        let chemical = chemicalsList[indexPath.row]
        
        cell.chemLabel.text = chemical.text
        
        return cell
    }

